			tl.staggerFromTo("#drop-area [id^='drop-a'] [id^='big-drop'] path", 0.5, {
				opacity: 0
			}, {
				opacity: 1,
				ease: Sine.easeOut
			}, -0.04, "drops+=1");

			tl.fromTo("#drop-shadow", 3, {
				opacity: 0
			}, {
				opacity: 1,
				ease: Sine.easeOut
			}, "drops+=1.5");

			tl.staggerFromTo("#droplet-groups g ellipse", 0.3, {
				scaleX: 0,
				scaleY: 1,
				opacity: 1
			}, {
				transformOrigin: "20% 50%",
				scaleX: 4,
				scaleY: 2,
				opacity: 0,
				repeat: 3,
				repeatDelay: 1,
				ease: Sine.easeIn
			}, 0.5, "drops+=0.3");

			tl.staggerFromTo("#drop-area [id^='drop-a'] [id^='drop-in'] ellipse", 0.3, {
				scaleX: 0,
				scaleY: 1,
				opacity: 1
			}, {
				transformOrigin: "20% 50%",
				scaleX: 4,
				scaleY: 2,
				repeat: 2,
				repeatDelay: 1,
				opacity: 0,
				ease: Sine.easeIn
			}, 0.5, "drops+=0.3");

			tl.staggerFromTo("#ripple ellipse", 2, {
				scale: 0,
				opacity: 1,
				transformOrigin: "50% 50%",
			}, {
				transformOrigin: "50% 50%",
				scale: 1,
				opacity: 0,
				ease: Sine.easeOut
			}, "-0.2", "drops+=0.3");

		}
	}
</script>